                    <Button variant="primary" type="button">
                        10 - 10.30
                    </Button>
                </Col>
                <Col style={{marginBottom:'3px'}} xs={6} sm={6} md={12} lg={4}>
                    <Button variant="primary" type="button">
                        10 - 10.30
                    </Button>
                </Col>
                <Col style={{marginBottom:'3px'}} xs={6} sm={6} md={12} lg={4}>
                    <Button variant="primary" type="button">
                        10 - 10.30
                    </Button>
                </Col>
            </Row>
        </Form>
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={this.props.hide}>Close</Button>
      </Modal.Footer>
    </Modal>
    </>)
}

}

